use rand::prelude::*;
use proconio::{input, marker::Usize1};
use svg::node::{element::{Rectangle, Image, Definitions, Use, Group, Title}, Text};
use std::collections::VecDeque;
use itertools::*;

pub trait SetMinMax {
	fn setmin(&mut self, v: Self) -> bool;
	fn setmax(&mut self, v: Self) -> bool;
}
impl<T> SetMinMax for T where T: PartialOrd {
	fn setmin(&mut self, v: T) -> bool {
		*self > v && { *self = v; true }
	}
	fn setmax(&mut self, v: T) -> bool {
		*self < v && { *self = v; true }
	}
}

#[macro_export]
macro_rules! mat {
	($($e:expr),*) => { Vec::from(vec![$($e),*]) };
	($($e:expr,)*) => { Vec::from(vec![$($e),*]) };
	($e:expr; $d:expr) => { Vec::from(vec![$e; $d]) };
	($e:expr; $d:expr $(; $ds:expr)+) => { Vec::from(vec![mat![$e $(; $ds)*]; $d]) };
}

pub const D: usize = 30;
pub const T: usize = 300;
pub const DIR: [(usize, usize); 4] = [(!0, 0), (1, 0), (0, !0), (0, 1)];
pub const DIR_CHAR: [char; 5] = ['U', 'D', 'L', 'R', '.'];

pub type Output = Vec<(String, Vec<String>)>; // (human_move, comments)

pub struct Input {
	pub ps: Vec<(usize, usize, usize)>,
	pub hs: Vec<(usize, usize)>,
	pub seed: u64,
}

impl std::fmt::Display for Input {
	fn fmt(&self, f: &mut std::fmt::Formatter<'_>) -> std::fmt::Result {
		writeln!(f, "{}", self.ps.len())?;
		for (x, y, t) in &self.ps {
			writeln!(f, "{} {} {}", x + 1, y + 1, t)?;
		}
		writeln!(f, "{}", self.hs.len())?;
		for (x, y) in &self.hs {
			writeln!(f, "{} {}", x + 1, y + 1)?;
		}
		Ok(())
	}
}

pub fn parse_input(f: &str) -> Input {
	let f = proconio::source::once::OnceSource::from(f);
	input! {
		from f,
		ps: [(Usize1, Usize1, usize)],
		hs: [(Usize1, Usize1)],
		seed: u64
	}
	Input { ps, hs, seed }
}

pub fn parse_output(_input: &Input, f: &str) -> Output {
	let mut out = vec![];
	let mut comments = vec![];
	for line in f.lines() {
		let line = line.trim();
		if line.starts_with('#') {
			comments.push(line[1..].trim().to_owned());
		} else if line.len() > 0 {
			out.push((line.to_owned(), comments));
			comments = vec![];
		}
	}
	out
}

pub fn bfs(blocked: &Vec<Vec<bool>>, sx: usize, sy: usize) -> Vec<Vec<usize>> {
	let mut dist = mat![!0; D; D];
	let mut que = VecDeque::new();
	que.push_back((sx, sy));
	dist[sx][sy] = 0;
	while let Some((x, y)) = que.pop_front() {
		let t = dist[x][y];
		for d in 0..4 {
			let x2 = x + DIR[d].0;
			let y2 = y + DIR[d].1;
			if x2 < D && y2 < D && !blocked[x2][y2] && dist[x2][y2].setmin(t + 1) {
				que.push_back((x2, y2));
			}
		}
	}
	dist
}

#[derive(Copy, Clone, Debug, PartialEq, Eq, PartialOrd, Ord)]
pub enum Pet {
	Cow,
	Pig,
	Rabbit,
	Dog(usize),
	Cat(usize, usize),
}

pub struct Sim {
	pub blocked: Vec<Vec<bool>>,
	pub rng: rand_chacha::ChaCha20Rng,
	pub ps: Vec<(usize, usize, Pet)>,
	pub hs: Vec<(usize, usize)>,
	pub turn: usize,
}

impl Sim {
	pub fn new(input: &Input) -> Self {
		Self {
			blocked: mat![false; D; D],
			rng: rand_chacha::ChaCha20Rng::seed_from_u64(input.seed),
			ps: input.ps.iter().map(|&(x, y, t)| (x, y, match t {
				1 => Pet::Cow,
				2 => Pet::Pig,
				3 => Pet::Rabbit,
				4 => Pet::Dog(!0),
				5 => Pet::Cat(!0, !0),
				_ => panic!("invalid input")
			})).collect(),
			hs: input.hs.clone(),
			turn: 0,
		}
	}
	pub fn human_move(&mut self, out: &str) -> Result<(), String> {
		self.turn += 1;
		if out.len() != self.hs.len() {
			return Err(format!("illegal output length (turn {})", self.turn));
		}
		let mut new_hs = self.hs.clone();
		for (i, c) in out.chars().enumerate() {
			match c {
				'.' => (),
				c if c.is_ascii_lowercase() => {
					if let Some(dir) = ['u', 'd', 'l', 'r'].iter().position(|&d| d == c) {
						let x = self.hs[i].0 + DIR[dir].0;
						let y = self.hs[i].1 + DIR[dir].1;
						if x >= D || y >= D {
							continue;
						}
						for j in 0..self.ps.len() {
							if self.ps[j].0 == x && self.ps[j].1 == y {
								return Err(format!("trying to block a square containing a pet (turn {})", self.turn));
							}
						}
						for j in 0..self.hs.len() {
							if self.hs[j].0 == x && self.hs[j].1 == y {
								return Err(format!("trying to block a square containing a human (turn {})", self.turn));
							}
						}
						for d in 0..4 {
							let x2 = x + DIR[d].0;
							let y2 = y + DIR[d].1;
							if x2 < D && y2 < D && self.ps.iter().any(|&(x, y, _)| (x2, y2) == (x, y)) {
								return Err(format!("trying to block a square whose adjacent square contains a pet (turn {})", self.turn));
							}
						}
						self.blocked[x][y] = true;
					} else {
						return Err(format!("illegal output char {} (turn {})", c, self.turn))
					}
				}
				c if c.is_ascii_uppercase() => {
					if let Some(dir) = DIR_CHAR.iter().position(|&d| d == c) {
						new_hs[i].0 += DIR[dir].0;
						new_hs[i].1 += DIR[dir].1;
						if new_hs[i].0 >= D || new_hs[i].1 >= D {
							return Err(format!("trying to move to an impassible square (turn {})", self.turn));
						}
					} else {
						return Err(format!("illegal output char {} (turn {})", c, self.turn))
					}
				}
				_ => return Err(format!("illegal output char {} (turn {})", c, self.turn))
			}
		}
		self.hs = new_hs;
		for i in 0..self.hs.len() {
			if self.blocked[self.hs[i].0][self.hs[i].1] {
				return Err(format!("trying to move to an impassible square (turn {})", self.turn));
			}
		}
		Ok(())
	}
	pub fn standard_move(&mut self, x: &mut usize, y: &mut usize) -> usize {
		let mut cand = vec![];
		for d in 0..4 {
			let x2 = *x + DIR[d].0;
			let y2 = *y + DIR[d].1;
			if x2 < D && y2 < D && !self.blocked[x2][y2] {
				cand.push(d);
			}
		}
		let d = *cand.choose(&mut self.rng).unwrap();
		*x += DIR[d].0;
		*y += DIR[d].1;
		d
	}
	pub fn pet_move(&mut self) -> String {
		let mut ps = self.ps.clone();
		let ret = ps.iter_mut().map(|(x, y, p)| {
			match p {
				Pet::Cow => {
					vec![DIR_CHAR[self.standard_move(x, y)]]
				},
				Pet::Pig => {
					let mut ret = vec![];
					for _ in 0..2 {
						ret.push(DIR_CHAR[self.standard_move(x, y)]);
					}
					ret
				},
				Pet::Rabbit => {
					let mut ret = vec![];
					for _ in 0..3 {
						ret.push(DIR_CHAR[self.standard_move(x, y)]);
					}
					ret
				},
				Pet::Dog(target) => {
					loop {
						if *target == !0 || self.hs[*target] == (*x, *y) {
							let dist = bfs(&self.blocked, *x, *y);
							let cand = (0..self.hs.len()).filter(|&i| dist[self.hs[i].0][self.hs[i].1] != !0 && dist[self.hs[i].0][self.hs[i].1] > 0).collect::<Vec<_>>();
							if let Some(i) = cand.choose(&mut self.rng) {
								*target = *i;
							} else {
								*target = !0;
								return vec![DIR_CHAR[self.standard_move(x, y)]];
							}
						}
						let (tx, ty) = self.hs[*target];
						let dist = bfs(&self.blocked, tx, ty);
						if dist[*x][*y] == !0 {
							*target = !0;
							continue;
						}
						let mut cand = vec![];
						for d in 0..4 {
							let x2 = *x + DIR[d].0;
							let y2 = *y + DIR[d].1;
							if x2 < D && y2 < D && dist[*x][*y] > dist[x2][y2] {
								cand.push(d);
							}
						}
						let dir = *cand.choose(&mut self.rng).unwrap();
						*x += DIR[dir].0;
						*y += DIR[dir].1;
						if self.hs[*target] == (*x, *y) {
							*target = !0;
						}
						let ret = vec![DIR_CHAR[dir], DIR_CHAR[self.standard_move(x, y)]];
						if *target != !0 && self.hs[*target] == (*x, *y) {
							*target = !0;
						}
						return ret;
					}
				},
				Pet::Cat(tx, ty) => {
					loop {
						if *tx != !0 && self.blocked[*tx][*ty] {
							*tx = !0;
							*ty = !0;
						}
						if *tx == !0 {
							let dist = bfs(&self.blocked, *x, *y);
							let mut cand = vec![];
							for i in 0..D {
								for j in 0..D {
									if dist[i][j] != !0 && dist[i][j] > 0 {
										cand.push((i, j));
									}
								}
							}
							let (i, j) = *cand.choose(&mut self.rng).unwrap();
							*tx = i;
							*ty = j;
						}
						let dist = bfs(&self.blocked, *tx, *ty);
						if dist[*x][*y] == !0 {
							*tx = !0;
							*ty = !0;
							continue;
						}
						let mut cand = vec![];
						for d in 0..4 {
							let x2 = *x + DIR[d].0;
							let y2 = *y + DIR[d].1;
							if x2 < D && y2 < D && dist[*x][*y] > dist[x2][y2] {
								cand.push(d);
							}
						}
						let dir = *cand.choose(&mut self.rng).unwrap();
						*x += DIR[dir].0;
						*y += DIR[dir].1;
						if (*tx, *ty) == (*x, *y) {
							*tx = !0;
							*ty = !0;
						}
						let ret = vec![DIR_CHAR[dir], DIR_CHAR[self.standard_move(x, y)]];
						if (*tx, *ty) == (*x, *y) {
							*tx = !0;
							*ty = !0;
						}
						return ret;
					}
				}
			}
		}).map(|s| s.iter().collect::<String>()).join(" ");
		self.ps = ps;
		ret
	}
	pub fn compute_score(&self) -> i64 {
		let mut score = 0.0;
		for &(x, y) in &self.hs {
			let dist = bfs(&self.blocked, x, y);
			let mut r = 0;
			for i in 0..D {
				for j in 0..D {
					if dist[i][j] != !0 {
						r += 1;
					}
				}
			}
			let mut n = 0;
			for &(px, py, _) in &self.ps {
				if dist[px][py] != !0 {
					n += 1;
				}
			}
			score += r as f64 / (30.0 * 30.0) * 2.0f64.powi(-n);
		}
		(1e8 * score / self.hs.len() as f64).round() as i64
	}
}

pub fn gen(seed: u64) -> Input {
	let mut rng = rand_chacha::ChaCha20Rng::seed_from_u64(seed ^ 386617);
	let mut free = vec![];
	for i in 0..D {
		for j in 0..D {
			free.push((i, j));
		}
	}
	let N = rng.gen_range(10i32, 21) as usize;
	let mut ps = vec![];
	for _ in 0..N {
		let k = rng.gen_range(0, free.len() as i32) as usize;
		ps.push((free[k].0, free[k].1, rng.gen_range(1i32, 6) as usize));
		free.swap_remove(k);
	}
	let M = rng.gen_range(5i32, 11) as usize;
	let mut hs = vec![];
	for _ in 0..M {
		let k = rng.gen_range(0, free.len() as i32) as usize;
		hs.push(free[k]);
		free.swap_remove(k);
	}
	let seed = rng.gen();
	Input { ps, hs, seed }
}

fn rect(x: i32, y: i32, w: i32, h: i32, fill: &str) -> Rectangle {
	Rectangle::new().set("x", x).set("y", y).set("width", w).set("height", h).set("fill", fill)
}

pub fn vis_default(input: &Input, out: &Output) -> (String, String) {
	let mut sim = Sim::new(input);
	let mut err = String::new();
	for i in 0..T {
		if let Err(e) = sim.human_move(&out[i].0) {
			err = e;
			break;
		}
		sim.pet_move();
	}
	(vis(input, &sim), err)
}

