pub fn vis(input: &Input, sim: &Sim) -> String {
	const W: i32 = 30;
	let mut doc = svg::Document::new().set("id", "vis").set("viewBox", (0, 0, D as i32 * W, D as i32 * W)).set("width", D as i32 * W).set("height", D as i32 * W);
	
	doc = doc.add(Definitions::new()
		.add(Image::new().set("id", "human").set("width", W).set("height", W).set("image-rendering", "pixelated").set("href", IMAGES[0]))
		.add(Image::new().set("id", "block").set("width", W).set("height", W).set("image-rendering", "pixelated").set("href", IMAGES[1]))
		.add(Image::new().set("id", "cow").set("width", W).set("height", W).set("image-rendering", "pixelated").set("href", IMAGES[2]))
		.add(Image::new().set("id", "pig").set("width", W).set("height", W).set("image-rendering", "pixelated").set("href", IMAGES[3]))
		.add(Image::new().set("id", "rabbit").set("width", W).set("height", W).set("image-rendering", "pixelated").set("href", IMAGES[4]))
		.add(Image::new().set("id", "dog").set("width", W).set("height", W).set("image-rendering", "pixelated").set("href", IMAGES[5]))
		.add(Image::new().set("id", "cat").set("width", W).set("height", W).set("image-rendering", "pixelated").set("href", IMAGES[6])));
	
	doc = doc.add(rect(0, 0, D as i32 * W, D as i32 * W, "white"));
	for i in 0..D {
		for j in 0..D {
			doc = doc.add(Group::new().add(rect(j as i32 * W + 1, i as i32 * W + 1, W - 2, W - 2, "silver")).add(Title::new().add(Text::new(format!("({}, {})", i + 1, j + 1)))));
			if sim.blocked[i][j] {
				doc = doc.add(Group::new().add(Use::new().set("x", j as i32 * W).set("y", i as i32 * W).set("href", "#block")).add(Title::new().add(Text::new(format!("({}, {})", i + 1, j + 1)))));
			}
		}
	}
	for i in 0..sim.hs.len() {
		doc = doc.add(Group::new().add(Use::new().set("x", sim.hs[i].1 as i32 * W).set("y", sim.hs[i].0 as i32 * W).set("href", "#human")).add(Title::new().add(Text::new(format!("({}, {})\nhuman {}", sim.hs[i].0 + 1, sim.hs[i].1 + 1, i + 1)))))
	}
	for i in 0..sim.ps.len() {
		let name = ["#cow", "#pig", "#rabbit", "#dog", "#cat"][input.ps[i].2 - 1];
		let title = match sim.ps[i].2 {
			Pet::Cow => format!("pet {} (cow)", i + 1),
			Pet::Pig => format!("pet {} (pig)", i + 1),
			Pet::Rabbit => format!("pet {} (rabbit)", i + 1),
			Pet::Dog(t) => format!("pet {} (dog)\ntarget: {}", i + 1, if t == !0 { "none".to_owned() } else { (t + 1).to_string() }),
			Pet::Cat(tx, ty) => format!("pet {} (cat)\ntarget: {}", i + 1, if tx == !0 { "none".to_owned() } else { format!("({}, {})", tx + 1, ty + 1) }),
		};
		doc = doc.add(Group::new().add(Use::new().set("x", sim.ps[i].1 as i32 * W).set("y", sim.ps[i].0 as i32 * W).set("href", name)).add(Title::new().add(Text::new(&format!("({}, {})\n{}", sim.ps[i].0 + 1, sim.ps[i].1 + 1, title)))));
	}
	doc.to_string()
}
